import React from "react";
import { motion } from "framer-motion";

const greeting = "Hello, I am Prithvi!";
const bio1 = "A frontend developer";
const bio2 = "specialized in React";

const LandingSection = () => (
  <div
    style={{
      display: "flex",
      justifyContent: "space-between",
      alignItems: "center",
      height: "100vh",
      background: "linear-gradient(to right, #7928CA, #FF0080)",
      color: "white",
      padding: "0 20px",
      boxSizing: "border-box",
    }}
  >
    <div style={{ flex: 1 }}>
      <div style={{ marginBottom: "20px" }}>
        <h1 style={{ fontSize: "24px" }}>{greeting}</h1>
      </div>
      <div style={{ marginBottom: "20px" }}>
        <p style={{ fontSize: "18px" }}>{bio1}</p>
      </div>
      <div style={{ marginBottom: "40px" }}>
        <p style={{ fontSize: "18px" }}>{bio2}</p>
      </div>
      <div>
        <button
          style={{
            backgroundColor: "transparent",
            border: "2px solid white",
            color: "white",
            padding: "10px 20px",
            fontSize: "16px",
            cursor: "pointer",
          }}
          onClick={() => window.open('/path-to-resume.pdf', '_blank')}
        >
          Download Resume
        </button>
      </div>
    </div>

    <motion.div
      initial={{ opacity: 0, x: 20 }}
      animate={{ opacity: 1, x: 0 }}
      transition={{ duration: 0.5 }}
    >
      <img
        src="path-to-laptop-animation.gif"
        alt="Laptop Animation"
        style={{ width: "300px" }}
      />
    </motion.div>
  </div>
);

export default LandingSection;
