import React from "react";
import { Avatar, Heading, VStack } from "@chakra-ui/react";
import FullScreenSection from "./FullScreenSection";
import { motion } from "framer-motion";

const greeting = "Hello, I am Prithvi!";
const bio1 = "A frontend developer";
const bio2 = "specialized in React";

const LandingSection = () => (
  <FullScreenSection
    justifyContent="center"
    alignItems="center"
    isDarkBackground
    bgGradient='linear(#7928CA, #FF0080)'
    w='200%' 
  >
     <>
      <script
        src="https://unpkg.com/@lottiefiles/lottie-player@latest/dist/lottie-player.js"
        crossOrigin="anonymous"
      ></script>
      <lottie-player
        src="https://lottie.host/a1b1c1c0-271c-4cac-b498-d3de097d917d/vsm0o6EbsH.json"
        background="#FFFFFF"
        speed="1"
        style={{ width: '300px', height: '300px' }}
        loop
        controls
        autoplay
        direction="1"
        mode="normal"
      ></lottie-player>
    </>
    <VStack spacing={8} align="center">

      <motion.div
        initial={{ opacity: 0, y: -20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.5 }}
      >
        <Avatar name="Prithvi" src="https://i.pravatar.cc/150?img=7" size="2xl" />
      </motion.div>

      <motion.div
        initial={{ opacity: 0, y: -20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.5, delay: 0.2 }}
      >
        <Heading size="md" color="white">
          {greeting}
        </Heading>
      </motion.div>

      <motion.div
        initial={{ opacity: 0, y: -20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.5, delay: 0.4 }}
      >
        <Heading size="2xl" color="white">
          {bio1}
        </Heading>
      </motion.div>

      <motion.div
        initial={{ opacity: 0, y: -20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.5, delay: 0.6 }}
      >
        <Heading size="2xl" color="white">
          {bio2}
        </Heading>
      </motion.div>

    </VStack>
  </FullScreenSection>
);

export default LandingSection;
