import React from "react";
import { useFormik } from "formik";
import {
  Box,
  Button,
  FormControl,
  FormErrorMessage,
  FormLabel,
  Heading,
  Input,
  Textarea,
  VStack,
} from "@chakra-ui/react";
import * as Yup from 'yup';
import FullScreenSection from "./FullScreenSection";
import { useAlertContext } from "../context/alertContext";

const LandingSection = () => {
  const { onOpen } = useAlertContext();

  const formik = useFormik({
    initialValues: {
      user_name: "",
      user_email: "",
      message: "",
    },
    onSubmit: (values) => {
      sendEmail(values);
      formik.resetForm();
    },
    validationSchema: Yup.object({
      user_name: Yup.string().required("Required"),
      user_email: Yup.string().email("Invalid email address").required("Required"),
      message: Yup.string().required("Required").min(25, "Must be at least 25 characters"),
    }),
  });

  const sendEmail = (values) => {
    emailjs.sendForm('service_d5c98am', 'template_enp2ojk', values, 'Tr43lutQeOqjpVL1_')
      .then((result) => {
        console.log(result.text);
        // Handle success, e.g., show a success alert
        onOpen({ type: "success", message: "Email sent successfully!" });
      })
      .catch((error) => {
        console.error(error.text);
        // Handle errors, e.g., show an error alert
        onOpen({ type: "error", message: "Error sending email. Please try again later." });
      });
  };

  return (
    <FullScreenSection
      isDarkBackground
      backgroundColor="#512DA8"
      py={16}
      spacing={8}
    >
      <VStack w="1024px" p={32} alignItems="flex-start">
        <Heading as="h1" id="contactme-section">
          Contact me
        </Heading>
        <Box p={6} rounded="md" w="100%">
          <form onSubmit={formik.handleSubmit}>
            <VStack spacing={4}>
              <FormControl isInvalid={formik.touched.user_name && formik.errors.user_name}>
                <FormLabel htmlFor="user_name">Name</FormLabel>
                <Input
                  id="user_name"
                  name="user_name"
                  {...formik.getFieldProps("user_name")}
                />
                <FormErrorMessage>{formik.errors.user_name}</FormErrorMessage>
              </FormControl>
              <FormControl isInvalid={formik.touched.user_email && formik.errors.user_email}>
                <FormLabel htmlFor="user_email">Email Address</FormLabel>
                <Input
                  id="user_email"
                  name="user_email"
                  type="email"
                  {...formik.getFieldProps("user_email")}
                />
                <FormErrorMessage>{formik.errors.user_email}</FormErrorMessage>
              </FormControl>
              <FormControl isInvalid={formik.touched.message && formik.errors.message}>
                <FormLabel htmlFor="message">Your message</FormLabel>
                <Textarea
                  id="message"
                  name="message"
                  height={250}
                  {...formik.getFieldProps("message")}
                />
                <FormErrorMessage>{formik.errors.message}</FormErrorMessage>
              </FormControl>
              <Button type="submit" colorScheme="purple" width="full" isLoading={formik.isSubmitting}>
                Submit
              </Button>
            </VStack>
          </form>
        </Box>
      </VStack>
    </FullScreenSection>
  );
};

export default LandingSection;
