// FullScreenSection.js
import React from "react";
import { Box } from "@chakra-ui/react";

const FullScreenSection = ({ children, ...rest }) => (
  <Box
    width="100vw"
    height="100vh"
    position="relative"
    overflow="hidden"
    {...rest}
  >
    {/* Starry Night Background Image */}
    <Box
      position="absolute"
      top="0"
      left="0"
      width="100%"
      height="100%"
      backgroundImage="url('path/to/starry-background.jpg')" // Replace with the actual path to your starry night background image
      backgroundSize="cover"
      backgroundPosition="center"
      filter="blur(5px)" // Optional: Add a slight blur effect to the background
      zIndex="-1" // Ensure the background stays behind other content
    />

    {/* Content */}
    {children}
  </Box>
);

export default FullScreenSection;
