import React from "react";



const LandingSection = props => {
  const headerStyle = {
    margin: 0,
    padding: 0,
    boxSizing: 'border-box',
  };

  const bodyStyle = {
    height: '100%',
    color: '#252934',
    background: '#fafafa',
    fontSize: '62.5%',
    fontFamily: "'Overlock', Arial, Helvetica, sans-serif",
    overflowX: 'hidden',
  };

  const linkStyle = {
    color: '#252934',
    fontSize: '1.4rem',
    textDecoration: 'none',
    transition: '200ms',
  };

  const linkHoverStyle = {
    color: '#f300b4',
  };

  return (
    <header id="welcome-section" style={headerStyle}>
      <div className="forest" />
      <div className="silhouette" />
      <div className="moon" />
      <div className="container">
        <h1>
          <span className="line">I do</span>
          <span className="line">graphic design</span>
          <span className="line">
            <span className="color">&amp;</span> code.
          </span>
        </h1>
        <div className="buttons">
          <a href="#projects" style={linkStyle}>
            my portfolio
          </a>
          <a href="#contact" className="cta" style={{ ...linkStyle, ...linkHoverStyle }}>
            get in touch
          </a>
        </div>
      </div>
    </header>
  );
};






export default LandingSection;
