import React from "react";
import { Avatar, Button, Heading, VStack } from "@chakra-ui/react";
import Lottie from 'lottie-react';
import Animation from '../asset/Animation - 1703010727908.json'
import FullScreenSection from "./FullScreenSection";
import { motion } from "framer-motion";

const greeting = "Hello, I am Prithvi!";
const bio1 = "A frontend developer";
const bio2 = "specialized in React";

const LandingSection = () => (
  <FullScreenSection
    justifyContent="space-between"
    alignItems="center"
    isDarkBackground
    bgGradient='linear(#7928CA, #FF0080)'
    w='100%'
    paddingX={8}
  >
    <VStack spacing={8} align="center" flex={1}>
      <motion.div
        initial={{ opacity: 0, x: -20 }}
        animate={{ opacity: 1, x: 0 }}
        transition={{ duration: 0.5 }}
      >
        <Avatar name="Prithvi" src="https://i.pravatar.cc/150?img=7" size="2xl" />
      </motion.div>

      <motion.div
        initial={{ opacity: 0, x: -20 }}
        animate={{ opacity: 1, x: 0 }}
        transition={{ duration: 0.5, delay: 0.2 }}
      >
        <Heading size="md" color="white">
          {greeting}
        </Heading>
      </motion.div>

      <motion.div
        initial={{ opacity: 0, x: -20 }}
        animate={{ opacity: 1, x: 0 }}
        transition={{ duration: 0.5, delay: 0.4 }}
      >
        <Heading size="2xl" color="white">
          {bio1}
        </Heading>
      </motion.div>

      <motion.div
        initial={{ opacity: 0, x: -20 }}
        animate={{ opacity: 1, x: 0 }}
        transition={{ duration: 0.5, delay: 0.6 }}
      >
        <Heading size="2xl" color="white">
          {bio2}
        </Heading>
      </motion.div>

      <motion.div
        initial={{ opacity: 0, x: -20 }}
        animate={{ opacity: 1, x: 0 }}
        transition={{ duration: 0.5, delay: 0.8 }}
      >
        <Button colorScheme="teal" size="lg" onClick={() => window.open('/path-to-resume.pdf', '_blank')}>
          Download Resume
        </Button>
      </motion.div>
    </VStack>

    <motion.div
      initial={{ opacity: 0, x: 20 }}
      animate={{ opacity: 1, x: 0 }}
      transition={{ duration: 0.5 }}
    >
      <Lottie Animation={Animation} />
    </motion.div>
  </FullScreenSection>
);

export default LandingSection;
